import React, { Component }  from 'react';
import logo from './logo.svg';
import './App.css';

class Table extends Component {
    constructor(props){
      super(props);
      this.state = {properti:[{no:0,nama:"Alfan",alamat:"Jalan Pahlawan",hobi:"Memancing"}]};
    }

    render(){
      return(
      <div className="container">
        <table style={{margin: 20+'px'}} className="table table-hovered table-striped table-bordered">
          <thead>
            <tr>
              <th>No</th><th>Nama</th><th>Alamat</th><th>Hobi</th>
            </tr>
          </thead>
          <tbody>
            {this.state.properti.map((val,index) => (
              <tr>
                <td>{index}</td>
                <td>{val.nama}</td>
                <td>{val.alamat}</td>
                <td>{val.hobi}</td>
              </tr>
            ))}
          </tbody>
        </table>
        <InputData />
      </div>
      );
    }
}

export default Table;

class InputData extends Component {
  constructor(props){
    super(props);
  }

  render(){
    return(
      <div className="col-md-3">
      <h3>Tambah Data</h3>
      <form>
        <div className="form-group">
          <label htmlFor="nama">Nama</label>
          <input id="nama" name="nama" type="text" className="form-control" />
        </div>
      </form></div>
    );
  }
}
